mapper.select:
select * from checkin where 1 = 1 

	and name like '%${name}%' 


	and status = #{status}  


	and roomId = #{roomId}  


	and roomTypeId = #{roomTypeId}  


	and idCard like '%${idCard}%' 


	and mobile like '%${mobile}%' 


	limit #{offset},#{pageSize}

select count(*) from book_order where 1=1  

	and name like '%${name}%' 


	and status = #{status}  


	and roomId = #{roomId}  


	and roomTypeId = #{roomTypeId}  


	and idCard like '%${idCard}%' 


	and mobile like '%${mobile}%'
